changeSet.createProcedure:
CREATE OR REPLACE FUNCTION ${POSTGRES_SCHEMA}.audit_shrdty() RETURNS trigger AS $tab_audit$
  DECLARE
    v_app_user_id    UUID;
    v_transaction_id UUID;
    v_operation_dtm  TIMESTAMP WITH TIME ZONE;
  BEGIN
    IF TG_OP = 'DELETE' THEN
      v_operation_dtm := now();
      v_app_user_id := NULL;
    ELSE
      SELECT  app_user_id 
      INTO    v_app_user_id
      FROM    ${POSTGRES_SCHEMA}.app_user
      WHERE   user_auth_id = NEW.updated_by
      ;

      IF NOT FOUND THEN
        v_app_user_id := NULL;
      END IF;

      v_operation_dtm := NEW.updated_dtm;
    END IF;

    INSERT INTO ${POSTGRES_SCHEMA}.aud_sheriff_duty VALUES (tg_op, v_operation_dtm, v_app_user_id, NULL, OLD.*);

    RETURN NULL;
  END;
$tab_audit$ LANGUAGE plpgsql;
